prompt --application/shared_components/navigation/lists/iot_tree_performance_list
begin
wwv_flow_imp_shared.create_list(
 p_id=>wwv_flow_imp.id(96966642330048840)
,p_name=>'IoT Tree Performance List'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'    select  null as "level",',
'            initcap(iot_element)||''(s)'' as label,',
'            null as c3,',
'            null as "Icon name",',
'            null c5,',
'            null as "img alt txt",',
'            iot_apex.compact_number(count(*)) as total_count',
'    from    iot_hierarchy',
'    where   root_id = :P35_ID ',
'    and     parent_id is not null',
'    group by iot_element,',
'            root_id',
'    union all',
'    select  null as "level",',
'            ''Raw Messages'' as Label,',
'            null as c3,',
'            null as "Icon name",',
'            null c5,',
'            null as "img alt txt",',
'            iot_apex.compact_number(count(*)) as total_count',
'    from    raw_data_syn rd,',
'            iot_hierarchy ih',
'    where   digital_twin_instance_id = ih.id',
'    and     root_id = :P35_ID',
'    group by ih.root_id',
'    union all',
'    select  null as "level",',
'            ''Rejected Messages'' as Label,',
'            null as c3,',
'            null as "Icon name",',
'            null c5,',
'            null as "img alt txt",',
'            iot_apex.compact_number(count(*)) as total_count',
'    from    rejected_data_syn rd,',
'            iot_hierarchy ih',
'    where   digital_twin_instance_id = ih.id',
'    and     root_id = :P35_ID',
'union all',
'    select  null as "level",',
'            ''Historized Messages'' as Label,',
'            null as c3,',
'            null as "Icon name",',
'            null c5,',
'            null as "img alt txt",',
'            iot_apex.compact_number(count(*)) as total_count',
'    from    (   select  distinct h.id,',
'                        h.time_observed,',
'                        ih.root_id',
'                from    historized_data_syn h,',
'                        iot_hierarchy ih',
'                where   ih.id = h.digital_twin_instance_id ',
'                and     ih.root_id = :P35_ID )',
'    group by root_id'))
,p_list_status=>'PUBLIC'
,p_version_scn=>45296582139607
);
end;
