prompt --application/shared_components/logic/application_processes/credential_check
begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(19602330693443080)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'credential_check'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    v_count     number;',
'    v_result    varchar2(4);',
'    v_user_ocid varchar2(100) := apex_application.g_x01;',
'    v_tenancy_ocid varchar2(100) := apex_application.g_x02;',
'    v_private_key varchar2(4000) := apex_application.g_x03;',
'    v_fingerprint varchar2(100) := apex_application.g_x04;',
'    v_region varchar2(100) := apex_application.g_x05;',
'    begin',
'        select  count(*)',
'        into    v_count',
'        from    all_credentials',
'        where   credential_name = ''P91_TEST_CREDENTIAL'';',
'    ',
'        if v_count != 0 then',
'            dbms_cloud.drop_credential(''P91_TEST_CREDENTIAL'');',
'        end if;',
'',
'        DBMS_CLOUD.CREATE_CREDENTIAL (',
'            credential_name   => ''P91_TEST_CREDENTIAL'',',
'            user_ocid         => v_user_ocid,',
'            tenancy_ocid      => v_tenancy_ocid,',
'            private_key       => v_private_key,',
'            fingerprint       => v_fingerprint );',
'        ',
'        v_result := iot_apex.CRED_CHK( ''P91_TEST_CREDENTIAL'',v_region );',
'        ',
'        dbms_cloud.drop_credential(''P91_TEST_CREDENTIAL'');',
'',
'        if v_result = ''GOOD'' then',
'            htp.prn(''Credential Test Successful'');',
'        elsif v_result = ''BAD'' then',
'            htp.prn(''Credential Test Failed'');',
'        else',
'            htp.prn(''Unknown Error: ''||v_result);',
'        end if;',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_version_scn=>45592385327679
);
end;
