prompt --application/shared_components/logic/application_processes/settings_submit
begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(28518115202983662)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'settings_submit'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    v_gx clob := apex_application.g_x01;',
'    v_json json_object_t;',
'    v_clob clob;',
'    jmain json_object_t;',
'    jo json_object_t;',
'    ja json_array_t;',
'begin',
'',
'    v_json := json_object_t.parse(v_gx);',
'',
'    ja := json_array_t();',
'    jmain := json_object_t();',
'',
'    jmain.put( ''iot_compartment'', v_json.get_string( ''IOT_COMPARTMENT'' ) );',
'',
'    jo := json_object_t();',
'    jo.put( ''name'', v_json.get_string(''TENANCY_NAME'') );',
'    jo.put( ''region'', v_json.get_string(''TENANCY_REGION'') );',
'    jo.put( ''ocid'', v_json.get_string(''TENANCY_OCID'') );',
'',
'    jmain.put( ''tenancy'', jo );',
'',
'    jo := json_object_t();',
'    jo.put( ''compartment'', v_json.get_string(''VAULT_COMPARTMENT'') );',
'    jo.put( ''ocid'', v_json.get_string(''VAULT_OCID'') );',
'    jo.put( ''master_key'', v_json.get_string(''VAULT_MASTER_KEY'') );',
'',
'    jmain.put( ''vault'', jo );',
'',
'    ja := json_array_t();',
'    ja := TREAT(v_json.get(''cert_auth_container'') AS json_array_t);',
'',
'    FOR i IN 0..ja.get_size-1 LOOP',
'        if length( ja.get(i).to_string )-2 <= 0 THEN',
'            ja.remove(i);',
'        end if;',
'    END LOOP;',
'',
'    jmain.put( ''cert_auth_container'', ja );',
'',
'    v_clob := jmain.to_clob();',
'',
'    dbms_output.put_line( v_clob ) ;',
' ',
'    UPDATE iot_config ic',
'    SET    ic.json_content = JSON_MERGEPATCH(ic.json_content, v_clob)',
'    where  json_topic = ''IOT_CONFIG'';',
'',
'    htp.p(''good'');',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_security_scheme=>'MUST_NOT_BE_PUBLIC_USER'
,p_version_scn=>45501618949792
);
