,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select  data_type,',
'        datetime,',
'        max(message_count)',
'from    (',
'        select  data_type,',
'               trunc(time_received,''HH'') as datetime,',
'                count(*) as message_count',
'        from    (',
'                    select  ''Raw Messages'' as data_type,',
'                            digital_twin_instance_id,',
'                            time_received',
'                    from    raw_data_syn rd,',
'                            iot_hierarchy mit',
'                    where   digital_twin_instance_id = mit.id',
'                    and     time_received > systimestamp - interval ''1'' day',
'                    and     parent_id = :P11_ID',
'                    union ALL',
'                    select  ''Rejected Messages'' as data_type,',
'                            digital_twin_instance_id,',
'                            time_received',
'                    from    rejected_data_syn rd,',
'                            iot_hierarchy mit',
'                    where   digital_twin_instance_id = mit.id',
'                    and     parent_id = :P11_ID',
'                    and     time_received > systimestamp - interval ''1'' day',
'                    union ALL',
'                    select  distinct ''Historized Messages'' as data_type,',
'                            i.digital_twin_instance_id,',
'                            time_observed',
'                    from    historized_data_syn i,',
'                            iot_hierarchy mit',
'                    where   mit.id = i.digital_twin_instance_id ',
'                    and     time_observed > systimestamp - interval ''1'' day',
'                    and     parent_id = :P11_ID',
'                )',
'        where time_received > systimestamp - interval ''1'' day',
'        group by data_type,',
'                trunc(time_received,''HH'')',
'        union all ',
'        select  ''Raw Messages'' as data_type,',
'                trunc((sysdate-level/24), ''HH24''),',
'                0',
'        from    dual ',
'        connect by rownum < 25',
'        union all ',
'        select  ''Rejected Messages'' as data_type,',
'                trunc((sysdate-level/24), ''HH24''),',
'                0',
'        from    dual ',
'        connect by rownum < 25',
'        union all ',
'        select  ''Historized Messages'' as data_type,',
'                trunc((sysdate-level/24), ''HH24''),',
'                0',
'        from    dual ',
'        connect by rownum < 25',
'        )',
'group by data_type,',
'        datetime',
'order by data_type,',
'        datetime'))
