,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with recs as (',
'    select  iot_element as label,',
'            iot_apex.compact_number(count(*)) as counts',
'    from    iot_hierarchy',
'    where   root_id = :P35_ID ',
'    and     parent_id is not null',
'    group by iot_element,',
'            root_id',
'    union all',
'    select  ''Raw_Messages'' as label,',
'            iot_apex.compact_number(count(*)) as counts',
'    from    raw_data_syn rd,',
'            iot_hierarchy ih',
'    where   digital_twin_instance_id = ih.id',
'    and     root_id = :P35_ID',
'    union all',
'    select  ''Rejected_Messages'' as label,',
'            iot_apex.compact_number(count(*)) as counts',
'    from    rejected_data_syn rd,',
'            iot_hierarchy ih',
'    where   digital_twin_instance_id = ih.id',
'    and     root_id = :P35_ID',
'    union all',
'    select  ''Historized_Messages'' as label,',
'            iot_apex.compact_number(sum(counted)) as counts',
'    from    (',
'                select count(*) as counted',
'                from    (   select  distinct ih.id,',
'                                    time_observed,',
'                                    ih.root_id',
'                            from    historized_data_syn i,',
'                                    iot_hierarchy ih',
'                            where   ih.id = i.digital_twin_instance_id ',
'                            and     ih.root_id = :P11_ID )',
'                group by root_id',
'                union all',
'                select 0 as counted from dual',
'            )',
'    union all',
'    select  ''Command_Messages'' as label,',
'            iot_apex.compact_number(count(*)) as counts',
'    from    raw_command_data_syn rd,',
'            iot_hierarchy ih',
'    where   digital_twin_instance_id = ih.id',
'    and     root_id = :P35_ID',
')',
'',
'SELECT  nvl( "Adapter(s)", 0 ) as "Adapter(s)",',
'        nvl( "Instances(s)", 0 ) as "Instances(s)",',
'        "Raw_Messages",',
'        "Rejected Messages",',
'        "Historized Messages",',
'        "Command Messages"',
'FROM recs',
'PIVOT',
'(',
'  max(counts)',
'  FOR label IN (''adapter'' as "Adapter(s)", ''instance'' as "Instances(s)", ''Raw_Messages'' as "Raw_Messages", ''Rejected_Messages'' as "Rejected Messages", ''Historized_Messages'' as "Historized Messages", ''Command_Messages'' as "Command Messages")',
');'))
